const openBtn=document.querySelectorAll("[data-open]"),closeBtn=document.querySelectorAll("[data-close]"),isVisible="is-visible";for(const el of openBtn)el.addEventListener("click",function(){var e=this.dataset.open;document.getElementById(e).classList.add(isVisible)});for(const el of closeBtn)el.addEventListener("click",function(){this.parentElement.parentElement.classList.remove(isVisible)});document.addEventListener("click",e=>{e.target==document.querySelector(".modal.is-visible")&&document.querySelector(".modal.is-visible").classList.remove(isVisible)}),document.addEventListener("keyup",e=>{"Escape"==e.key&&document.querySelector(".modal.is-visible")&&document.querySelector(".modal.is-visible").classList.remove(isVisible)});const bookingInputs=document.querySelectorAll(".form__input");for(el of bookingInputs)el.addEventListener("click",function(){var e=this.dataset.label;document.getElementById(e).classList.add("label-hide")});const progress=document.querySelector(".progress");progress.addEventListener("input",function(){var e=this.value;this.style.background=`linear-gradient(to right, #710707 0%, #710707 ${e}%, #fff ${e}%, white 100%)`}),console.log("Hello from appjs");